import React, { useEffect, useState, useContext } from "react";
import styles from "./AddLunchForm.module.css";
import UserContext from "../userContext";
import TextField from "@material-ui/core/TextField";
import { FormHelperText } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";

export default function AddLunchForm() {
  const userContext = useContext(UserContext);

  //   useEffect(() => {
  //     fetchProfile();
  //   }, []);

  function handleSubmit(event) {
    event.preventDefault();

    fetch(
      "http://localhost:4000/accounts/addlunch" +
        `?email=${userContext.user.email}`,
      {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors",
        cache: "no-cache",
        headers: {
          "Content-Type": "application/json",
          Authorization: userContext.user.token,
        },
        body: JSON.stringify({}),
      }
    )
      .then((response) => {
        console.log(response.status);

        return response.json();
      })
      .then((json) => {
        // setName(json.name);
        // console.log("json", json);
      })
      .catch((err) => console.log("err", err));
  }

  const handleChange = (event) => {
    // if (event.target.name === "email") setEmail(event.target.value);
    // if (event.target.name === "password") setPassword(event.target.value);
  };

  return (
    <div className={styles.Login}>
      <div className={styles.box}>
        <h1 className={styles.title}>Add lunch offert</h1>

        <form onSubmit={handleSubmit}>
          <div className={styles.container}>
            <div className={styles.smallerContainer}>
              <h1 className={styles.dishes}>Soups</h1>
              <br></br>
              <TextField
                classes={{ root: styles.CustomInput }}
                name="soup_price"
                label="Soup price"
                variant="outlined"
                onChange={handleChange}
              />
              <div>
                <TextField
                  classes={{ root: styles.CustomInput }}
                  name="Soup1"
                  label="Soup"
                  variant="outlined"
                  onChange={handleChange}
                />
                <AddIcon color="primary" />
                {/* <TextField
              classes={{ root: styles.CustomInput }}
              name="Soup2"
              label="Soup"
              variant="outlined"
              onChange={handleChange}
            /> */}
              </div>
            </div>
            <div className={styles.smallerContainer}>
              <h1 className={styles.dishes}>Main dishes</h1>
              <br></br>
              <TextField
                classes={{ root: styles.CustomInput }}
                name="dish_price"
                label="Main dish price"
                variant="outlined"
                onChange={handleChange}
              />
              <TextField
                classes={{ root: styles.CustomInput }}
                name="Dish1"
                label="Main dish"
                variant="outlined"
                onChange={handleChange}
              />
            </div>
            <div className={styles.smallerContainer}>
              <h1 className={styles.dishes}>Drinks</h1>
              <br></br>
              <TextField
                classes={{ root: styles.CustomInput }}
                name="drink_price"
                label="Drink price"
                variant="outlined"
                onChange={handleChange}
              />
              <TextField
                classes={{ root: styles.CustomInput }}
                name="Drink1"
                label="Drink"
                variant="outlined"
                onChange={handleChange}
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
