import React, { useEffect, useState, useContext, Fragment } from "react";
import styles from "./HomeRestaurant.module.css";
import UserContext from "../userContext";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import ButtonBase from "@material-ui/core/ButtonBase";

export default function HomeRestaurant() {
  const userContext = useContext(UserContext);
  const [allOffertsArray, setAllOffertsArray] = useState({});

  useEffect(() => {
    fetchAllOfferts();
  }, []);

  const fetchAllOfferts = () => {
    fetch("http://localhost:4000/restaurant/all_offerts", {
      method: "GET",
      mode: "cors",
      cache: "no-cache",
      headers: {
        "Content-Type": "application/json",
        Authorization: userContext.user.token,
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setAllOffertsArray(json.allOffertsArray);
      })
      .catch((err) => console.log("err", err));
  };

  const newTable = Object.entries(allOffertsArray);
  const datesTable = newTable.map((element, index) => element[0]);
  // let sortedDates = datesTable;
  // .sort((a, b) =>
  //   a.split("/").reverse().join().localeCompare(b.split("/").reverse().join())
  // )
  // .reverse();

  const valuestable = newTable.map((element, index) => [
    element[1].map((e, i) => [
      e.date,
      e.name,
      e.lunch_start_time,
      e.lunch_end_time,
      e.set_price,
    ]),
  ]);

  const tab = valuestable.map((e, i) => e[0]);
  // tab.map((element, index) => console.log(element[0]));

  const valTab = newTable.map((element, index) => 
    element[1][0]
  )

  valTab.map((e, i) => console.log(e))

  console.log(valTab);

  return (
    <div className={styles.bigBox}>
      <div className={styles.Box}>
        <h1 className={styles.title}>Lunche na dziś</h1>
        <div className={styles.container}>
          {datesTable.map((element, index) => (
            <div className={styles.Root} key={index}>
              <Paper className={styles.Paper}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm container>
                    <Grid item xs container direction="column" spacing={2}>
                      <Grid item xs>
                        {tab[index].map((e, i) => (
                          <div className={styles.PaperContainer}>
                            <div className={styles.PaperLeft}>
                              <Typography
                                variant="subtitle1"
                                // color="textSecondary"
                                className={styles.Text}
                              >
                                {e[1]}
                              </Typography>
                              <Typography
                                variant="body2"
                                color="textSecondary"
                                key={i}
                                className={styles.Text}
                              >
                                {e[2]} - {e[3]}
                              </Typography>
                              <Typography
                                variant="body2"
                                color="textSecondary"
                                className={styles.Text}
                              >
                                Set price: {e[4]}
                              </Typography>
                            </div>
                            <div className={styles.PaperRight}>
                              <Grid item>
                                <Typography variant="body2">
                                  {e[0]}
                                </Typography>
                              </Grid>
                            </div>
                          </div>
                        ))}
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              </Paper>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
