import React, { useEffect, useState, useContext } from "react";
import styles from "./ClientProfile.module.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import { FormHelperText } from "@material-ui/core";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import NavBar from "../NavBarComponent";
import UserContext from "../userContext";
import QrReader from "react-qr-reader";

export default function Profile() {
  const userContext = useContext(UserContext);
  const [stampsCounter, setStampsCounter] = useState();
  const [result, setResult] = useState("No result");
  const [fetchCounter, setFetchCounter] = useState(0);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = () => {
    fetch(
      "http://localhost:4000/accounts/clientprofile" +
        `?email=${userContext.user.email}`,
      {
        method: "GET", // *GET, POST, PUT, DELETE, etc.
        mode: "cors",
        cache: "no-cache",
        headers: {
          "Content-Type": "application/json",
          Authorization: userContext.user.token,
        },
      }
    )
      .then((response) => {
        console.log(response.status);

        return response.json();
      })
      .then((json) => {
        setStampsCounter(json.stamps_counter);
        // console.log("json", json);
      })
      .catch((err) => console.log("err", err));
  };

  const handleScan = (data) => {
    if (!!data) {
      console.log("data", data);
      setResult(data);
      if (fetchCounter !== 1) {
        console.log("fetchcounter", fetchCounter);
        fetchStampsCounter(data);
        setFetchCounter(1);
      }
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const fetchStampsCounter = (result) => {
    // const index = result.indexOf(",");
    // console.log(index);
    // console.log(result.slice(0, index));
    // console.log(result.slice(index + 1, result.length));
    const email = userContext.user.email;
    fetch("http://localhost:4000/accounts/stampsCounter", {
      method: "POST", // *GET, POST, PUT, DELETE, etc.
      mode: "cors",
      cache: "no-cache",
      headers: {
        "Content-Type": "application/json",
        Authorization: userContext.user.token,
      },
      body: JSON.stringify({
        email,
        result,
      }),
    })
      .then((response) => {
        console.log(response.status);
        return response.json();
      })
      .then((json) => {
        // setStampsCounter(json.stamps_counter);
        console.log("json", json);
      })
      .catch((err) => console.log("err", err));
  };

  return (
    <div className={styles.Login}>
      <div className={styles.container}>
        <h1 className={styles.title}>{localStorage.getItem("account_type")}</h1>
        <a>email: {userContext.user.email}</a>
        <br></br>
        <a>stamps counter: {stampsCounter}</a>
        <div>
          <QrReader
            className={styles.CodeReader}
            delay={300}
            onError={handleError}
            onScan={handleScan}
          />
          <p>{result}</p>
        </div>
      </div>
    </div>
  );
}
